import { PromiseProvider } from 'mongoose';
import App from './App.js';
import React, { useState, useEffect} from 'react'

function Button(props) {
    const [username, getUserName] = useState(props.username)
    const [password, getPassword] = useState(props.password)
    const [db, getDatabase] = useState(props.db)


    useEffect(() => {
        console.log(`Username: ${username}, passu: ${password}, db: ${db}`,)
    })
    
    return(
    <button onClick={() => getUserName(), getPassword(), getDatabase()}>
        Connect
    </button>
    )
}


export default Button;
